// App.js
import React, { useState } from 'react';
import PaymentForm from './components/PaymentForm.js';
import PaymentStatusCheck from './components/PaymentStatusCheck.js';

const App = () => {
    const [paymentReference, setPaymentReference] = useState('');
    const [paymentStatus, setPaymentStatus] = useState('');
    const [error, setError] = useState(null);

    return (
        <div>
            <PaymentForm setPaymentReference={setPaymentReference} setPaymentStatus={setPaymentStatus} setError={setError} />
            <PaymentStatusCheck paymentReference={paymentReference} setPaymentStatus={setPaymentStatus} setError={setError} />

            {/* Display error if any */}
            {error && (
                <div style={{ color: 'red' }}>
                    <p>Error Message: {error.message}</p>
                    <p>Payment Reference: {error.paymentReference}</p>
                </div>
            )}

            {/* Display payment status */}
            {paymentStatus && <p>Payment Status: {paymentStatus}</p>}
        </div>
    );
};

export default App;
