// PaymentStatusCheck.js
import React, { useState, useEffect } from 'react';

const PaymentStatusCheck = ({ paymentReference, setPaymentStatus, setError }) => {
    const [status, setStatus] = useState(null);

    useEffect(() => {
        const checkPaymentStatus = async () => {
            try {
                // Clear previous errors
                setError(null);

                // Validate payment reference
                if (!paymentReference) {
                    return;
                }

                // Make a GET request to check payment status
                const response = await fetch(`http://localhost:8081/check-payment-status/${paymentReference}`);

                if (!response.ok) {
                    throw await response.json();
                }

                const data = await response.json();
                setStatus(data);
                setPaymentStatus(data); // Assuming you want to set the payment status in App component
            } catch (error) {
                setError(error);
            }
        };

        checkPaymentStatus();
    }, [paymentReference, setPaymentStatus, setError]);

    return (
        <div>
            {/* Display payment status */}
            {status && <p>Payment Status: {status}</p>}
        </div>
    );
};

export default PaymentStatusCheck;
